mapper.update:
UPDATE transaction
receiver_Name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr = #{divyAddr}, dlvy_date = #{divyDate}, dlvy_request = #{divyRequest}, payment_option = #{paymentOption},
WHERE tran_No = #{tranNo}

mapper.select:
SELECT *
FROM(SELECT inner_table.* , ROWNUM AS row_seq 
			FROM( SELECT tr.tran_no, tr.prod_no, tr.buyer_id, tr.payment_option, tr.receiver_name, tr.receiver_phone, tr.demailaddr, tr.dlvy_request, tr.tran_status_code, tr.order_data, tr.dlvy_date, tr.is_purchase_code, tr.quantity , pr.price
							FROM transaction tr, product pr
							WHERE tr.prod_no = pr.prod_no AND pr.prod_no= #{prodNo}
							 ) inner_table
				WHERE ROWNUM <= #{search.endRowNum} )
			WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}
